/**
 * This groovy places the unit in yard position specified on receive transaction
 *
 * Author: Peter Seiler
 * Date: 06/29/15
 * JIRA: CSDV-3063
 * SFDC: 142561
 *
 * 7/2/2015 - Insert UNIT_RECEIVE and UNIT_IN_GATE events into the units' history.
 *
 * 7/12/2015 - Remove 'executeInternal'
 *
 * 7/30/2015 - Add code to update UCC and ECC codes
 * 
 * 9/2/2015 - Remove the ECC and UCC code maitenance 
 *
 * 10/16/2015 BG - Added validation for UNIT_IN_GATE through service business rule check
 */

public class MATPlaceUnitInYard extends AbstractGateTaskInterceptor implements EGateTaskInterceptor

{
    public void execute(TransactionAndVisitHolder inDao)

    {
        this.log("Execution Started MATPlaceUnitInYard");


        /* check various components of the gate transaction to insure everything needed is present. */

        if (inDao == null)
            return;

        TruckVisitDetails ThisTV = inDao.getTv();

        if (ThisTV == null)
            return;

        /* Get the trasnactions from the truck visit. */

        Set<TruckTransaction> TheseTransactions = ThisTV.getTvdtlsTruckTrans();

        if (TheseTransactions != null && !TheseTransactions.isEmpty())
        {

            Yard ThisYard = ContextHelper.getThreadYard();

            for (TruckTransaction ThisTran : TheseTransactions)
            {

                if (ThisTran.isReceival() && ThisTran.getTranStatus() == TranStatusEnum.COMPLETE)
                {

                    /* get the position specified in the transaction */

                    UnitFacilityVisit ThisUFV = ThisTran.getTranUfv();

                    String ThisYardRow = ThisTran.getTranFlexString03();
					println("INGATE EMPTY ThisYard"+ThisYard);
					println("INGATE EMPTY ThisYardRow"+ThisYardRow);
                    LocPosition ThisCtrPos = LocPosition.createYardPosition(ThisYard, ThisYardRow, null, null, false);

                    /* rectify the unit into that yard position */

                    RectifyParms thisRectifyParm = new RectifyParms();
					println("INGATE EMPTY 1"+UfvTransitStateEnum.S40_YARD);
					println("INGATE EMPTY 2"+UnitVisitStateEnum.ACTIVE);
					println("INGATE EMPTY 3"+ThisCtrPos);
                    thisRectifyParm.setEraseHistory(false);
                    thisRectifyParm.setUfvTransitState(UfvTransitStateEnum.S40_YARD);
                    thisRectifyParm.setUnitVisitState(UnitVisitStateEnum.ACTIVE);
                    thisRectifyParm.setPosition(ThisCtrPos);
                    println("INGATE EMPTYthisRectifyParm"+thisRectifyParm);
                    ThisUFV.rectify(thisRectifyParm);

                    /* insert a unit_receive event into the history */

                    ServicesManager sm = (ServicesManager) Roastery.getBean(ServicesManager.BEAN_ID);

                    Unit ThisUnit = ThisTran.getTranUnit();


                    EventType InGateEventType = EventType.findEventType('UNIT_IN_GATE');

                    if (sm != null && InGateEventType != null && ThisUnit != null)
                    {
						BizViolation bizViolation = sm.verifyEventAllowed(InGateEventType, ThisUnit);
                        if (bizViolation!= null) {
                            RoadBizUtil.appendExceptionChainAsWarnings(bizViolation);
                           //throw bizViolation;
                        }
                        (IEvent) sm.recordEvent(InGateEventType, "Unit received ", null, null, ThisUnit, null, null);
                    }

                    EventType receiveEventType = EventType.findEventType('UNIT_RECEIVE');

                    if (sm != null && receiveEventType != null && ThisUnit != null)
                    {
                        (IEvent) sm.recordEvent(receiveEventType, "Unit received ", null, null, ThisUnit, null, null);
                    }
                }
            }
        }
    }
}

/**
 * Author: Karthik Rajendran
 * Created date: 06/29/2013
 * Description: This Groovy sets the Consignee, Shipper fields to upper case and invoke update MatUpdateUnitConsigneeNotes groovy
 * 
 */

import com.navis.external.framework.entity.AbstractEntityLifecycleInterceptor
import com.navis.external.framework.entity.EEntityView
import com.navis.external.framework.util.EFieldChanges
import com.navis.external.framework.util.EFieldChangesView
import com.navis.inventory.business.units.GoodsBase
import com.navis.inventory.business.units.Unit
import com.navis.apex.business.model.GroovyInjectionBase

public class ConsigneeShipperNamesToUpper extends AbstractEntityLifecycleInterceptor {
	public void onUpdate(EEntityView inEntity, EFieldChangesView inOriginalFieldChanges, EFieldChanges inMoreFieldChanges) {
		this.log("START : ******* ConsigneeShipperNamesToUpper.onUpdate() *******")
		try {
			Unit unit =  inEntity._entity
			this.log("Unit ID = "+unit.unitId+"\t BLNBR = "+unit.getFieldValue("unitGoods.gdsBlNbr"))
			GoodsBase goods = unit.getUnitGoods()
			def inj = new GroovyInjectionBase()
			// Shipper Name To Upper case
			def unitShipper = unit.getFieldValue("unitGoods.gdsShipperAsString")
			def unitShipperUpper = null
			if(!unitShipper.equals(null)) {
				unitShipperUpper = unitShipper.toUpperCase()
				if(!unitShipper.equals(unitShipperUpper)) {
					unit.setFieldValue("unitGoods.gdsShipperBzu.bzuName",unitShipperUpper)
					unit.setFieldValue("unitGoods.gdsShipperBzu.bzuId",unitShipperUpper)
				}
			}
			this.log("ORIG SHIPPER="+unitShipper+"\t -> \t NEW SHIPPER="+unitShipperUpper)
			// Consignee Name To Upper case
			def unitConsignee = unit.getFieldValue("unitGoods.gdsConsigneeAsString")
			def unitConsigneeUpper = null
			if(!unitConsignee.equals(null)) {
				unitConsigneeUpper = unitConsignee.toUpperCase()
				boolean isOrigConsigneeUpper = unitConsignee.equals(unitConsigneeUpper)
				if(!isOrigConsigneeUpper) {
					unit.setFieldValue("unitGoods.gdsConsigneeBzu.bzuName",unitConsigneeUpper)
					unit.setFieldValue("unitGoods.gdsConsigneeBzu.bzuId",unitConsigneeUpper)
				}
			}
			this.log("ORIG CONSIGNEE="+unitConsignee+"\t -> \t NEW CONSIGNEE="+unitConsigneeUpper)
			// Consignee Notes Update
			def MatUpdateUnitConsigneeNotes = inj.getGroovyClassInstance("MatUpdateUnitConsigneeNotes")
			MatUpdateUnitConsigneeNotes.updateNotes(unit)
		}
		catch (Throwable e) {
			this.log("ConsigneeShipperNamesToUpper.onUpdate(): error occured while attempting to set ShipperName/ConsigneeName field to uppercase \n" + e.getMessage())
			e.printStackTrace()
		}
		this.log("END : ******* ConsigneeShipperNamesToUpper.onUpdate() *******")
	}
}

import com.navis.argo.business.model.Facility;
import com.navis.external.framework.entity.AbstractEntityLifecycleInterceptor
