/**
 * Created by VNatesan on 7/19/2016.
 * Purpose of this class is to create event for strip during transload  funtionality
 */
class MATCallTransloadStrip extends AbstractGeneralNoticeCodeExtension{

    private ServicesManager _sm = (ServicesManager) Roastery.getBean(ServicesManager.BEAN_ID);

    public void execute(GroovyEvent inEvent){

        this.log("Execution Started MATCallTransloadStrip");
        //get the event
        Event thisEvent = inEvent.getEvent();
        GroovyApi groovyApi = new GroovyApi();
        if (thisEvent == null) {
            return;
        }
        //get the Unit
        def unit =  inEvent.getEntity();
        if (unit == null) {
            return;
        }

        Set fieldChanges = thisEvent.getEvntFieldChanges();
        final UserContext userContext = ContextHelper.getThreadUserContext();
        PersistenceTemplate template = new PersistenceTemplate(userContext);
        MessageCollector mc = template.invoke(new CarinaPersistenceCallback() {
            @Override
            protected void doInTransaction() {
                try {
                    TransactionParms.getBoundParms().setUserContext(userContext);
                    String evnt= "UNIT_STRIP";
                    EventType event = EventType.findEventType(evnt);
                    if (event != null && !_sm.hasEventTypeBeenRecorded(event, unit)) {
                        Date eventTime = new Date(System.currentTimeMillis());
                        _sm.recordEvent(event, "sending unit strip ", null, null, unit, (FieldChanges) null, eventTime);
                        }

                }
                finally {
                    // restore original user context
                    TransactionParms.getBoundParms().setUserContext(userContext);
                }
            }
        });
    }
}

