import com.navis.services.business.event.GroovyEvent;
import org.apache.log4j.Logger;

public class MatMassUpdateVesselPositionExt extends AbstractGeneralNoticeCodeExtension {
    public void execute(GroovyEvent inEvent) {
        GroovyApi api = new GroovyApi();
        Event thisEvent = inEvent.getEvent();
        MessageCollector collector = getMessageCollector();

        if (thisEvent == null)
            return;

        Unit inUnit = (Unit) inEvent.getEntity();

        if (inUnit == null)
            return;

        this.log("UPDATE POSITION MatMassUpdateVesselPositionExt executed for "+inUnit.getUnitId());
        String inSlot = thisEvent.getEvntNote();
        this.log("Updating Position for : "+inUnit.getUnitId() +" with postion <<"+thisEvent.getEvntNote()+">>");
        if (inUnit != null && inSlot != null) {
            UnitFacilityVisit ufv = inUnit.getUfvForFacilityLiveOnly(ContextHelper.getThreadFacility());
            if (ufv!= null ) {
                UnitManager unitManager = (UnitManager) Roastery.getBean(UnitManager.BEAN_ID);
                try {
                    BizRequest request = new BizRequest(getUserContext());
                    Serializable[] ufvGkeys = new Serializable[1];
                    ufvGkeys[0] = ufv.getUfvGkey();
                    FieldChanges fieldChanges = new FieldChanges();

                    FieldChange change = new FieldChange(InventoryBizMetafield.UNIT_DIGITS, inUnit.getUnitId());
                    fieldChanges.setFieldChange(change);

                    change = new FieldChange(InventoryBizMetafield.UNIT_SLOT, thisEvent.getEvntNote());
                    fieldChanges.setFieldChange(change);
                    CrudOperation crud = new CrudOperation(null, CrudOperation.TASK_UPDATE, InventoryEntity.UNIT_FACILITY_VISIT, fieldChanges, ufvGkeys);
                    request.addCrudOperation(crud);
                    BizResponse response = new BizResponse();
                    INVENTORY_FACADE.recordSlotCorrection(request, response);
                    collector.getMessages().addAll(response.getMessages());
                    this.log("Position updated for "+ inUnit.getUnitId());

                    //unitManager.recordUnitYardMove(ufv, inSlot, (String) null);
                } catch (BizViolation bizViolation) {
                    this.log("Cannot record yard move "+bizViolation.toString());
                    String userId = ContextHelper.getThreadUserId();
                    String emailTo = "1aktosdevteam@matson.com";
                    if (userId!= null) {
                        BaseUser baseUser = BaseUser.findBaseUser(userId);
                        if (baseUser!= null && baseUser.getBuserEMail()!=null) {
                            emailTo = baseUser.getBuserEMail();
                        }
                    }
                    api.sendEmail(emailTo, emailTo,"Test Update Position MatMassUpdateVesselPositionExt Failed", " Update of position failed for unit "+ufv.getUfvUnit().getUnitId() +" with error"+bizViolation.toString());
                }
            }
        }
    }
    private static InventoryFacade INVENTORY_FACADE = (InventoryFacade) Roastery.getBean(InventoryFacade.BEAN_ID);

}

import com.navis.argo.ContextHelper
import com.navis.external.services.AbstractGeneralNoticeCodeExtension
import com.navis.framework.persistence.HibernateApi
import com.navis.inventory.business.units.EqBaseOrderItem
import com.navis.inventory.business.units.Unit
import com.navis.inventory.business.units.UnitFacilityVisit
import com.navis.services.business.event.Event
import com.navis.services.business.event.GroovyEvent
public class MATUpdateTempSettingOnUnitRoll extends AbstractGeneralNoticeCodeExtension {
    public void execute(GroovyEvent inEvent) {
        this.log("Execution Started MATUpdateTempSettingOnUnitRoll");
        /* get the event */
        Event thisEvent = inEvent.getEvent();
                if (thisEvent == null)
            return;
        Unit unit = (Unit) inEvent.getEntity();
        if (unit == null)
            return;
        UnitFacilityVisit ufv = unit.getUfvForFacilityLiveOnly(ContextHelper.getThreadFacility());
        if (ufv == null) {
            return;
        }
        if ("UNIT_ROLL".equalsIgnoreCase(thisEvent.getEventTypeId())) {
            EqBaseOrderItem eqboi = unit.getUnitPrimaryUe().getUeDepartureOrderItem();
            if (eqboi != null) {
                // got the booking item, now get the remarks from it and copy to ufvFlexString07
                String strAux = eqboi.getEqoiRemarks();
                if (strAux != null) {
                    ufv.setUfvFlexString07(strAux);
                } else {
                    ufv.setUfvFlexString07("");
                }
            } else {
                ufv.setUfvFlexString07("");
            }
            HibernateApi.getInstance().saveOrUpdate(ufv);
        }
    }
}

import com.navis.argo.ContextHelper;
import com.navis.argo.business.api.GroovyApi;
import com.navis.argo.business.atoms.LocTypeEnum;
import com.navis.argo.business.model.CarrierVisit;
import com.navis.argo.business.reference.Group;
import com.navis.external.services.AbstractGeneralNoticeCodeExtension;
import com.navis.framework.persistence.HibernateApi;
import com.navis.inventory.business.units.Routing;
import com.navis.inventory.business.units.Unit;
import com.navis.inventory.business.units.UnitFacilityVisit;
import com.navis.services.business.event.Event;
import com.navis.services.business.event.GroovyEvent;
import com.navis.vessel.business.schedule.VesselVisitDetails;

public class MATUpdateGroupOnBargeDischargeLoad extends AbstractGeneralNoticeCodeExtension

    {
        public void execute(GroovyEvent inEvent){
        this.log("Execution Started MATUpdateGroupOnBargeDischargeLoad");

    /* get the event */

        Event thisEvent = inEvent.getEvent();
        GroovyApi groovyApi = new GroovyApi();
        if (thisEvent == null)
            return;

        Unit unit = (Unit) inEvent.getEntity();

        if (unit == null)
            return;

        UnitFacilityVisit ufv = unit.getUfvForFacilityLiveOnly(ContextHelper.getThreadFacility());
        if (ufv == null){
            return;
        }
        Routing routing = unit.getUnitRouting();
        CarrierVisit cv = null;
        if ("UNIT_DISCH".equalsIgnoreCase(thisEvent.getEventTypeId())) {
            cv = ufv.getInboundCarrierVisit();
        } else if ("UNIT_LOAD".equalsIgnoreCase(thisEvent.getEventTypeId())){
            cv = ufv.getUfvActualObCv();
        }

        // look only for the Inbound containers on a Vessel
        if (cv == null || !LocTypeEnum.VESSEL.equals(cv.getCvCarrierMode())){
            return;
        }

        // only look for a barge
        VesselVisitDetails vvd = VesselVisitDetails.resolveVvdFromCv(cv);
        boolean isUpdated = false;
        if (vvd == null || !vvd.isBarge() ||!cv.getCvId().startsWith("ILB")){
            if (routing!=null && routing.getRtgGroup() != null && "ILB".equalsIgnoreCase(routing.getRtgGroup().getGrpId())) {
                routing.setRtgGroup(null);
                isUpdated = true;
            }
        } else if (cv.getCvId().startsWith("ILB")) {
            if (routing!=null) {
                Group group = Group.findOrCreateGroup("ILB");
                routing.setRtgGroup(group);
                isUpdated = true;
            }
        }
        if (isUpdated) {
            unit.setUnitRouting(routing);
            HibernateApi.getInstance().saveOrUpdate(unit);
        }
    }
}

import com.navis.external.framework.AbstractExtensionCallback
import com.navis.framework.AllOtherFrameworkPropertyKeys
import com.navis.framework.util.BizViolation
import com.navis.inventory.business.units.EqBaseOrder
