/*

Groovy code called at the end of the truck outgate transaction. It submits an XML document to NOW Solutions Web service using SOAP

Amine Nebri, anebri@navis.com - June 25 2008

*/
/*
* Srno   Doer  Date          Desc
* A1     GR    04/21/2010    Added Method and Changes to pass ChasType and NO-EIT transactions
* A2     GR    06/04/2010    Added eitString='0' to allow PassPass Gate Trans to Go through
* A3     GR    11/01/2010    TOS2.1 : CHANGE FOR WS
*/

import com.navis.apex.business.model.GroovyInjectionBase
//import com.matson.nowsol.*
import N4Gate.matsongate_n4.*;

public class NOWOutgateProcessor  extends GroovyInjectionBase
{
	private static proxy = null;

	public void execute(inDao, api, departure)
	{
		println("\nNOWProcessor: --Executing Groovy Gate Task---")

		def eitString = inDao.tv.tvdtlsBatNbr

		// We exit if the Bat Number is not numeric or <100 and >200
		try
		 {
			if (eitString == null)
			 {
				println("NOWProcessor: Reassigning eit=0!\n\n")
				//return - A1
			}

			int eitId = eitString != null ? eitString.toInteger() : 0  //A1

			if (eitId < 101 || eitId >200)
			 {
				println("NOWProcessor: Skipping eit "+eitId+"!\n\n")
				//return --A1
			}
		}
		catch (Exception e)
		 {
			println("NOWProcessor: Skipping non numeric id  "+eitString+"!\n\n"+e)
			return 
		}

		def url = api.getGroovyClassInstance("GvyRefDataLookup").getNowServer();
		if(url == null) {
		      println("NOWOutgateProcessor: Skipping no now server  !\n\n");
           return;
		}

		// Build the message by calling NOWMessageBuilder
		def msg = api.getGroovyClassInstance("NOWMessageBuilder").formOutgateMessage(inDao)
		def result

		println("NOWOutgateProcessor: invoking NOW WebService with: " + msg)
		def time = System.currentTimeMillis();
		// Invoke NOW


		// 07/31/09 Switched to BEA service
		try {
		      if(proxy == null)  {
			  try {
				//def s = new N4Gate_Service_Impl(url);
				//proxy = s.getN4Gate()

				N4Gate_ServiceLocator gateSrvLoc = new N4Gate_ServiceLocator();
                gateSrvLoc.setN4GateEndpointAddress(url);
                proxy = gateSrvLoc.getN4Gate();  

			  } catch (Exception pe) {
				//def s = new N4Gate_Service_Impl(url);
				//proxy = s.getN4Gate()
				N4Gate_ServiceLocator gateSrvLoc = new N4Gate_ServiceLocator();
                gateSrvLoc.setN4GateEndpointAddress(url);
                proxy = gateSrvLoc.getN4Gate();  

			  }
		     }
		    api.log("Proxy="+proxy);

		    try {
			result = proxy.process_tracking(msg);
		    } catch (Exception re) {
			result = proxy.process_tracking(msg);
		    }
		}
		catch (Exception e) {
		    proxy = null;
		    e.printStackTrace();
		    throw new Exception("NOWOutgateProcessor: Could not connect to NOW: " + e.getClass());

                 }


		// Test for null result

		time = System.currentTimeMillis() - time;


 		println("NOWOutgateProcessor:("+(double)time/1000.0+") NOW result is: " + result)

		if (!result)
		 {
			throw new Exception("NOW WebService returned null")
		}

		// Examine the result document
		def resultDoc = new XmlParser().parseText(result)

		def status = resultDoc.'truck-departure-response'[0].'@status'

		println("NOWProcessor: status is " + status)

		if (status == "3") 
		{
			def error = resultDoc.'truck-departure-response'[0].messages.message[0].'@text'

			println("NOWOutgateProcessor: error is: " + error)
			throw new Exception(error)
		}

 		time = System.currentTimeMillis() - time;
		time = (double)time/1000.0;
		println("NOWOutgateProcessor: success! Parse Time="+time+"\n\n")
	}
}

/*

Groovy code called from the gate form to print or not print the document based on the logic below.
The logic fails the transaction with an error message if ufvFlexString02 is not Y and user entered a chassis accessory number

Amine Nebri, anebri@navis.com - July 4 2008

Setup:  BAT # is a required field at the Trk Visit IN stage.

If the BAT # entered is between 101 ? 200, print the Doc Type ID = EIT.
If the BAT # entered is between 701 ? 799, do not print anything (this is an in-house UTR, no ticket needed).
If the BAT # entered is any other value, print the Doc Type ID = NO EIT.

4/28/09: CB: Added conditionals for the create and print. Default is to do both.
                      However, for SI GATE, the tx's will do the create and the tv will do the print.

11/06/14: Peter Seiler Change PrintDocument and CreateDocument calls to use 2.6 API.
*/

import com.navis.apex.business.model.GroovyInjectionBase
import com.navis.road.business.adaptor.document.CreateDocument
import com.navis.road.business.adaptor.document.PrintDocument
import com.navis.road.business.api.GroovyRoadApi
import com.navis.road.business.workflow.TransactionAndVisitHolder
import com.navis.road.portal.GateConfigurationParameterConstants

public class CustomCreateAndPrintDocument extends GroovyInjectionBase
{
    public void execute(TransactionAndVisitHolder inDao, GroovyRoadApi api) {
        execute(inDao, api, true, true)
    }

    public void execute(TransactionAndVisitHolder inDao, GroovyRoadApi api, boolean isCreate, boolean isPrint)
    {

        def batString = inDao.tv.tvdtlsBatNbr


        if (batString == null) {
            api.log("CustomCreateAndPrintDocument null");
            return
        }

        api.log("CustomCreateAndPrintDocument for bat ${batString}");

        def batNumber = -99

        try
        {
            // We convert the string into an integer
            batNumber = batString.toInteger()
        }
        catch (Exception e)
        {
            // We exit if the Bat Number is not numeric
            //throw new Exception("BAT # must be numeric")
            return
        }

        // No need to print anything in this case. This is an in house UTR
        if ( batNumber >= 701 && batNumber <= 799)
            return

        def docTypeId = "NO EIT"

        if (batNumber >= 101 && batNumber <= 200)
            docTypeId = "EIT"

        // Set the doc type id and the pop-up value and number of copies
        inDao.put("docTypeId", docTypeId)

        // Create the document
        if (isCreate)
        {
            new CreateDocument().execute(inDao)
        }

        // Print the document
        if (isPrint)
        {
            inDao.put("displayPrintPopup", 'true')
            inDao.put("printNoOfCopies", '1')
            new PrintDocument().execute(inDao)
        }
    }
}

