      	GeneralReference generalReference = new GeneralReference();
      	for (int i = 0; i < inEdiBlItemArray.length; i++)
      	{
        	EdiBlEquipment ediBlEquipment = inEdiBlItemArray[i];

			EdiContainer ediContainer = ediBlEquipment.getEdiContainer();
