					}
			}

			if (temperature != null){
				prefTemp = temperature.getPreferredTemperature();
			}

			println("InputediContainer:::::::"+container);
			unitList = this.getUnit(container);
			if (unitList.size() > 0)
			{
				Iterator iterUnitList = unitList.iterator()
				while (iterUnitList.hasNext())
