				valueRetained = "";
				if (unitSealNbr != null){
					valueRetained = unitSealNbr;
					ediContainer.setContainerSealNumber1(unitSealNbr);
				}else{
					valueRetained = sealNbr;
				}
				retSealNbr = valueRetained;
			}
