      def gvyEditFlag = gvyBaseClass.getGroovyClassInstance("GvyCmisProcessEditFlag");
      misc2 =gvyEditFlag.processEditFlag(event, eventType, unitObj, gvyBaseClass )
      unitObj.setUnitFlexString11(misc2)
