public class GvyCmisEquipmentDeliveryOrder {

//Method Returns Equipment Delivey Order Object  
public EquipmentDeliveryOrder findEquipmentDeliveryOrder(String inEdoNbr) 
{
   EquipmentDeliveryOrder edo = null;
  try
  {
    //LineOperator line = LineOperator.resolveLineOprFromScopedBizUnit(inLine);
    DomainQuery dq = QueryUtils.createDomainQuery("EquipmentDeliveryOrder").addDqPredicate(PredicateFactory.eq(InventoryField.EQBO_NBR, inEdoNbr));
    List lst = HibernateApi.getInstance().findEntitiesByDomainQuery(dq);

    if (lst != null) {
        if (lst.size() == 1) {
           edo = (EquipmentDeliveryOrder)lst.get(0);
        }
    }
   }catch(Exception e){
      e.printStackTrace()
   }
    return edo;
 }

  //Method returns Line Operator for EDO (Object EDO extends EquipmentOrderHbr )
  public String getEDOLineOperator(EquipmentDeliveryOrder edo)
 {
     def edoLine = ''
      try{
         if(edo != null){
             edoLine = edo.getEqoLine().getBzuId()
         }
      }catch(Exception e){
         e.printStackTrace()
      }    
      return edoLine        
  }
}

import com.navis.inventory.business.api.UnitManager;
import com.navis.framework.persistence.HibernateApi;
import com.navis.framework.portal.QueryUtils;
import com.navis.framework.portal.query.DomainQuery;
import com.navis.framework.portal.query.PredicateFactory;
import com.navis.inventory.business.units.Unit;
import com.navis.inventory.business.api.UnitField;
import com.navis.inventory.business.atoms.UfvTransitStateEnum;
import com.navis.argo.business.api.GroovyApi;
import com.navis.framework.business.Roastery;
/*
* Get a list of all the Advised Chassis' and deletes them
*/
class GvyAdvisedChassisCleanupJob  {
    public String execute(Map inParameters) {

        GroovyApi gvyApi = new GroovyApi();

        gvyApi.logWarn("Advised chassis::starting...");

        DomainQuery dq = QueryUtils.createDomainQuery("Unit");
        dq.addDqPredicate(PredicateFactory.eq(UnitField.UNIT_CURRENT_UFV_TRANSIT_STATE, UfvTransitStateEnum.S10_ADVISED));
        dq.addDqPredicate(PredicateFactory.like(UnitField.UNIT_PRIMARY_EQTYPE_ID, "C%"));

        def list = HibernateApi.getInstance().findEntitiesByDomainQuery(dq);
        def iter = list.iterator();
        gvyApi.logWarn("Advised chassis: " + (list!=null ? list.size() : "0"));
        while(iter.hasNext()) {
            UnitManager unitManager = (UnitManager) Roastery.getBean(UnitManager.BEAN_ID);
            try {
                Unit chsUnit = (Unit) iter.next();
                unitManager.purgeUnit(chsUnit);
                gvyApi.logWarn("Delete Advised Chassis: "+chsUnit.getUnitId()+" /inUnit:"+chsUnit);
            } catch (Exception e) {
                e.printStackTrace();
            }

        }
    }
}

import com.navis.apex.business.model.GroovyInjectionBase
import com.navis.services.business.event.GroovyEvent
import com.navis.orders.business.eqorders.Booking
import com.navis.argo.business.model.CarrierVisit
import com.navis.argo.business.atoms.BizRoleEnum
import com.navis.argo.business.reference.*

class GvyInjAbob extends GroovyInjectionBase {
	public String execute(Map inParameters) {

		com.navis.argo.ContextHelper.setThreadExternalUser("ACETS");

		def eventId = (String) inParameters.get("eventId");
		def bookingNum = (String) inParameters.get( "bookingNum");
		def vesvoy = (String) inParameters.get( "vesvoy");
                                           log( "GvyInjAbob start event: " + eventId + " for booking: " + bookingNum);

			// find booking
			def facility = getFacility();
			def cv = CarrierVisit.findVesselVisit( facility, vesvoy);
			if ( cv == null) {
				fail( "ERR_GVY_BOB_001. Could not find the carrier visit: " + vesvoy);
			}

			def bizScope = ScopedBizUnit.findScopedBizUnit( "MAT", BizRoleEnum.LINEOP);
			if ( bizScope == null) {
					fail( "ERR_GVY_BOB_002. Could not find the business unit: MAT");
			}

			def booking = Booking.findBookingByUniquenessCriteria( bookingNum, bizScope, cv);
			if ( booking == null) {
					fail( "ERR_GVY_BOB_003. Could not find booking: " + bookingNum);
			}

		def event = new GroovyEvent( null, booking);

		event.postNewEvent( eventId);
		log( "GvyInjAbob post event: " + eventId + " for booking: " + bookingNum);
	}

}

