import java.io.Serializable;

import com.navis.argo.ArgoEntity;
import com.navis.argo.ArgoField;
import com.navis.argo.ContextHelper;
import com.navis.argo.business.xps.model.Che;
import com.navis.framework.persistence.HibernateApi;
import com.navis.framework.portal.QueryUtils;
import com.navis.framework.portal.query.DomainQuery;
import com.navis.framework.portal.query.PredicateFactory;
import com.navis.framework.util.internationalization.PropertyKey;
import com.navis.framework.util.internationalization.PropertyKeyFactory;
import com.navis.framework.util.message.MessageLevel;
import com.navis.road.business.model.TruckVisitDetails;
import com.navis.road.business.util.RoadBizUtil;
import com.navis.road.business.workflow.TransactionAndVisitHolder;

public class RejectInternalTruckUnknown {

    public static String BEAN_ID = "rejectInternalTruckUnknown";
    public static PropertyKey INTERNAL_TRUCK_ID_UNKNOWN = PropertyKeyFactory.valueOf("gate.internal_truck_id_unknown");

    public void execute(TransactionAndVisitHolder dao, api) {

        TruckVisitDetails tv = dao.getTv();
        if (tv == null) {
            return;
        }

        String truckLicenseNbr = tv.getTvdtlsTruckLicenseNbr();
api.log('TV DETAILS, LIC #: ' + truckLicenseNbr)
api.log('Event' + dao.properties);
api.log('TV' + tv.properties)
api.log('Tran' + dao.tran.properties)


        if (truckLicenseNbr == null) {
            return;
        }

        Serializable yardKey = ContextHelper.getThreadYardKey();

        Che xpeChe = findCheByShortName(truckLicenseNbr, yardKey);

        if (xpeChe == null || xpeChe.getCheKind() != INTERNAL_TRUCK) {
            RoadBizUtil.appendMessage(MessageLevel.SEVERE, INTERNAL_TRUCK_ID_UNKNOWN, truckLicenseNbr);
        }
    }

    private Che findCheByShortName(String shortName, Serializable yardKey) {
        DomainQuery dq = QueryUtils.createDomainQuery(ArgoEntity.CHE)
                .addDqPredicate(PredicateFactory.eq(ArgoField.CHE_YARD, yardKey))
                .addDqPredicate(PredicateFactory.eq(ArgoField.CHE_SHORT_NAME, shortName));

        return (Che) HibernateApi.getInstance().getUniqueEntityByDomainQuery(dq);
    }

    private static final Long INTERNAL_TRUCK = 3L;
}

import com.navis.argo.business.reference.Group;
import com.navis.framework.util.internationalization.PropertyKey;
import com.navis.framework.util.internationalization.PropertyKeyFactory;
import com.navis.framework.util.message.MessageLevel;
import com.navis.inventory.business.units.Routing;
import com.navis.inventory.business.units.Unit;
import com.navis.road.business.util.RoadBizUtil;
import com.navis.road.business.workflow.TransactionAndVisitHolder;

public class RejectContainerNotInGroup {

    public static final String BEAN_ID = "rejectContainerNotInGroup";
    public static PropertyKey CONTAINER_NOT_IN_GROUP = PropertyKeyFactory.valueOf("gate.container_not_in_group");

    public void execute(TransactionAndVisitHolder dao, api) {
        Unit unit = dao.getTran().getTranUnit();
        String groupId = getGroupId(unit);

        if (!GROUP_ID.equals(groupId)) {
            RoadBizUtil.appendMessage(MessageLevel.SEVERE, CONTAINER_NOT_IN_GROUP, dao.getTran().getTranCtrNbr(), groupId);
        }
    }

    private String getGroupId(Unit unit) {
        Routing routing = unit.getUnitRouting();
        if (routing == null) {
            return null;
        }

        Group group = routing.getRtgGroup();
        if (group == null) {
            return null;
        }

        return group.getGrpId();
    }

    private static final String GROUP_ID = "PASSPASS";
}

import com.navis.argo.ContextHelper;
import com.navis.argo.business.atoms.UnitCategoryEnum;
import com.navis.argo.business.reference.Container;
import com.navis.framework.business.Roastery;
import com.navis.framework.util.BizViolation;
import com.navis.framework.util.message.MessageCollector;
import com.navis.framework.util.message.MessageLevel;
import com.navis.inventory.business.api.UnitFinder;
import com.navis.inventory.business.atoms.UfvTransitStateEnum;
import com.navis.inventory.business.atoms.UnitVisitStateEnum;
import com.navis.inventory.business.units.Unit;
import com.navis.inventory.business.units.UnitFacilityVisit;
import com.navis.road.RoadPropertyKeys;
import com.navis.road.business.adaptor.IGateTaskAdaptor;
import com.navis.road.business.atoms.TranSubTypeEnum;
import com.navis.road.business.model.TruckAction;
import com.navis.road.business.model.TruckTransaction;
import com.navis.road.business.util.RoadBizUtil;
import com.navis.road.business.workflow.TransactionAndVisitHolder;
import java.util.Collection;
import java.util.Iterator;

public class GvyRejectTranAdvisedUnitMismatch
    implements IGateTaskAdaptor
{

    public GvyRejectTranAdvisedUnitMismatch()
    {
    }

    public void execute(TransactionAndVisitHolder inDao)
        throws BizViolation
    {
       println("GvyRejectTranAdvisedUnitMismatch");
        TruckTransaction tran = inDao.getTran();
        if(tran != null)
        {
            TranSubTypeEnum subType = tran.getTranSubType();
            Container ctr = tran.getTranContainer();
            println("GvyRejectTranAdvisedUnitMismatch subType "+subType + "::"+ctr);
            if(ctr != null)
            {
                UnitFinder unitFinder = (UnitFinder)Roastery.getBean("unitFinder");
                Collection unitList = unitFinder.findAllUnitsUsingEq(ContextHelper.getThreadComplex(), ctr);
                if(unitList == null) return;
                Iterator iterator = unitList.iterator();
                while(iterator.hasNext()) {
                    Unit unit = (Unit)iterator.next();
                    println("GvyRejectTranAdvisedUnitMismatch unit "+unit);
                    if(unit != null && unit.getUnitCategory() != null && (UnitVisitStateEnum.ADVISED.equals(unit.getUnitVisitState()) || UnitVisitStateEnum.ACTIVE.equals(unit.getUnitVisitState())))
                    {

                        UnitFacilityVisit ufv = unit.getUfvForFacilityNewest(ContextHelper.getThreadFacility());
                        println("GvyRejectTranAdvisedUnitMismatch ufv "+ufv);
                        if(ufv != null && tran.isReceival() && !tran.isAdvisedUnitCompatible(subType, unit) && ufv.isTransitStateAtMost(UfvTransitStateEnum.S20_INBOUND)) {
                            String[] fields = [ctr.getEqIdFull(), unit.getUnitCategory().getName(), subType.getName()];
                            RoadBizUtil.getMessageCollector().appendMessage(MessageLevel.SEVERE, RoadPropertyKeys.GATE__UNIT_PREADVISED_TRAN_TYPE_MISMATCH, null,  fields
                            );
                           }
                    }
                } 
            }
        }
    }

    public static String BEAN_ID = "rejectTranAdvisedUnitMismatch";

	@Override
	public TruckAction getDefaultAction() {
		// TODO Auto-generated method stub
		return null;
	}

}

public class GvyCmisEventEditPortBooking
{ 
    def cmisActnList = ''

    public String processEditPortBkg(String xmlData,Object event,Object gvyBaseClass)
    {
       def xmlGvyString = xmlData
       try
       {
         def unit = event.getEntity()
         def gvyCmisUtil = gvyBaseClass.getGroovyClassInstance("GvyCmisUtil"); 
         def gvyEventUtil = gvyBaseClass.getGroovyClassInstance("GvyEventUtil");  
         def rerouteEvntFeed = gvyBaseClass.getGroovyClassInstance("GvyCmisEventFeedUnitReroute")
         cmisActnList = gvyBaseClass.getGroovyClassInstance("GvyCmisListAction");  

          //1]GoodsBLNbr 2] DestPort 3] UnitNotes 4]DischPort 5] LoadPort 6] O/B carrier 
         def updtGdsBlNbrFlag = gvyEventUtil.wasFieldChanged(event, "gdsBlNbr")
         def updtDestportFlag = gvyEventUtil.wasFieldChanged(event, "gdsDestination")
         def updtUnitNotesFlag = gvyEventUtil.wasFieldChanged(event, "unitRemark") 
         def updtDischPortFlag = gvyEventUtil.wasFieldChanged(event, "rtgPOD1")
         def updtLoadPortFlag = gvyEventUtil.wasFieldChanged(event, "rtgOPL")
         def updtObCarrierFlag = gvyEventUtil.wasFieldChanged(event, "ufvIntendedObCv")

         def locationStatus = gvyCmisUtil.getFieldValues(xmlGvyString, "locationStatus=")      

         if (updtObCarrierFlag)
         {
             processOBCarrierChange(xmlGvyString, gvyBaseClass,gvyCmisUtil)     
         }
         if(updtGdsBlNbrFlag || updtDischPortFlag || updtLoadPortFlag)
         {
            postFieldChange(xmlGvyString,gvyBaseClass,gvyCmisUtil,locationStatus)
          }
          if(updtDestportFlag)
          {
            rerouteEvntFeed.processUnitRerouteCmisFeed(xmlGvyString,gvyBaseClass,event,unit,cmisActnList)  
          }
         if(updtUnitNotesFlag)
         {
            cmisActnList.setActionList("EDT")
         }


        //Post Cmis msg after appending the required action
        LinkedHashSet actionList = cmisActnList.getActionList();
        println("actionList :::::"+actionList.size())
        for(aAction in actionList)
        {
          println("UNIT_EDIT_PORT_BOOKING_POSTING ::"+aAction);
          gvyCmisUtil.postMsgForAction(xmlGvyString,gvyBaseClass,aAction)
        }

         println("<<<< processEditPortBkg(String xmlData,Object event,Object gvyBaseClass) >>>>")
      }catch(Exception e){
           e.printStackTrace()
      }

    }

    public void postFieldChange(String xmlData,Object gvyBaseClass,Object gvyCmisUtil,String locationStatus)
   {
         def xmlGvyString = xmlData
         try
         {	 
           if(locationStatus.equals('7'))
           {  
              //overwrite ACTION=NIB & Post Msg
              cmisActnList.setActionList("NIB")
           }
           else{
              //overwrite ACTION=BDC & Post Msg
              cmisActnList.setActionList("NIB")             
           }
         }catch(Exception e){
              e.printStackTrace();
         }
    }

  public void processOBCarrierChange(String xmlGvyData, Object gvyBaseClass,Object gvyCmisUtil)
  {
     def xmlGvyString = xmlGvyData 
    try
    {
      //OBCarrier Cmis Action   
      cmisActnList.setActionList("AVL")
      cmisActnList.setActionList("BDC")       
     }catch(Exception e){
       e.printStackTrace();
     }
  }

}

