   public String applyHold( Object unit, String note) {
      def servicesMgr = (ServicesManager)Roastery.getBean("servicesManager");

    //applyHold(String s, LogicalEntity logicalentity, String s1, String s2, boolean flag)
    servicesMgr.applyHold( "INGATE", unit, note, "null", true);
   }

    public String releaseHold( Object unit, String note) {
      def servicesMgr = (ServicesManager)Roastery.getBean("servicesManager");
     // servicesMgr.applyGuardedPermission( "INGATE", unit, null, null, note);
      deleteFlagType("INGATE",unit) 
    }


   public void deleteFlagType(String inFlagId, Object inLogicalEntity) 
  {
    FlagType flagType = FlagType.findFlagType(inFlagId);
    DomainQuery dq = QueryUtils.createDomainQuery("Flag").addDqPredicate(PredicateFactory.eq(ServicesField.FLAG_FLAG_TYPE, flagType.getFlgtypGkey())).addDqPredicate(PredicateFactory.eq(ServicesField.FLAG_APPLIED_TO_PRIMARY_KEY, inLogicalEntity.getPrimaryKey()))
    List flags = HibernateApi.getInstance().findEntitiesByDomainQuery(dq);
    for( i in flags) {
      Flag flag = (Flag) i;
      println("FLAG ----------------"+flag)
      ArgoUtils.carefulDelete(flag);
    }

 }  


}

import com.navis.road.business.model.TruckTransaction
import com.navis.road.business.workflow.TransactionAndVisitHolder
import com.navis.framework.util.internationalization.PropertyKey
import com.navis.framework.util.internationalization.PropertyKeyFactory
import com.navis.framework.util.message.MessageLevel
import com.navis.road.business.util.RoadBizUtil
import com.navis.argo.business.reference.RoutingPoint


public class GvyDrayInPodCheck{

  public void execute(TransactionAndVisitHolder dao, api){
   try{
       TruckTransaction tran = dao.tran
       def dest = tran.tranDestination
       def msg = [dest];
        //added 'ANK','DUT','KDK', todo remove other ports
       def drayInPorts = ['ANK','DUT','KDK','KAH','HIL','KHI','NAW','LNI','MOL','HON','GUM','SHA','NGB','XMN','SEA','OAK','LAX']
       if(drayInPorts.contains(dest)){
          RoutingPoint routingPoint = new RoutingPoint()
          tran.tranDischargePoint1 = routingPoint.findRoutingPoint(dest)
       }else{
        PropertyKey INVALID_DEST = PropertyKeyFactory.valueOf("gate.invalid.dest");
        RoadBizUtil.appendMessage(MessageLevel.SEVERE,INVALID_DEST,msg);
      }
   }catch(Exception e){
       def msg = [" Exception"];
       PropertyKey INVALID_DEST = PropertyKeyFactory.valueOf("gate.invalid.dest");
       RoadBizUtil.appendMessage(MessageLevel.SEVERE,INVALID_DEST,msg);
       e.printStackTrace()
    }

 }//Method Ends

}//Class Ends

import com.navis.argo.ContextHelper
import com.navis.argo.business.reference.Chassis
import com.navis.argo.business.model.LocPosition;
import com.navis.framework.business.Roastery
import com.navis.framework.persistence.HibernateApi
import com.navis.inventory.business.api.UnitFinder
import com.navis.inventory.business.api.UnitManager;
import com.navis.inventory.business.units.Unit
import com.navis.inventory.business.units.UnitEquipment
import com.navis.inventory.business.units.UnitFacilityVisit
import com.navis.road.business.model.TruckTransaction
import com.navis.road.business.workflow.TransactionAndVisitHolder
import com.navis.inventory.business.atoms.UfvTransitStateEnum
import com.navis.inventory.business.atoms.EqUnitRoleEnum
/**
 * detaches the chassis from any active units and creates a new in yard chassis unit ready for delivery
 */

public class CreateInYardChassis {


   public static String BEAN_ID = "createInYardChassis";

    public void execute(TransactionAndVisitHolder dao, api) {
        TruckTransaction tran = dao.tran;
        String chsNbr = tran.tranChsNbrAssigned;
        Chassis ch = Chassis.findChassis(chsNbr);

        if (!ch)
            return;

        UnitFinder unitFinder = (UnitFinder) Roastery.getBean(UnitFinder.BEAN_ID);
        UnitEquipment unitEq = unitFinder.findActiveUeUsingEqInAnyRole(null, ContextHelper.getThreadComplex(), ch);
        UnitManager unitMgr = (UnitManager) Roastery.getBean(UnitManager.BEAN_ID);

        Unit unit;
        UnitFacilityVisit ufv;

        if (unitEq != null) {
            // find the unit for bare chassis or the container with this chassis
            unit = unitEq.ueUnit
            ufv = unit.getUnitActiveUfvNowActive();
            if (ufv == null)
                  return;

            if (UfvTransitStateEnum.S40_YARD.equals(ufv.getUfvTransitState()) )
               return; // the bare chassis unit or the container unit with this chassis is already in the yard

            // retire the unit if it is a bare chassis unit by itself
            if (EqUnitRoleEnum.PRIMARY.equals(unitEq.getUeEqRole())) {
                unit.makeRetired();
                HibernateApi.getInstance().flush();
                return;
             } else {
                 // otherewise swipe with OWN chassis
                 unit.swipeChsByOwnersChs();
                 HibernateApi.getInstance().flush();
             } 
        }

        // create unit for the Chassis in the Yard
        UnitFacilityVisit newUfv = unitMgr.createYardBornUnit(ContextHelper.getThreadFacility(), ch, null, "groovy");
        newUfv.updateArrivePosition(LocPosition.createLocPosition(dao.getTv().getCvdCv(), null, null));
        newUfv.updateObCv(dao.getTv().getCvdCv());

        Unit newUnit = newUfv.getUfvUnit();
        HibernateApi.getInstance().flush();                 
   }   
}

