			{
				Iterator iterChList = chList.iterator()
				while (iterChList.hasNext())
				{
					def chassis = iterChList.next();
					println(chassis.eqIdFull+"|"+chassis.eqEquipType.eqtypId+"|"+chassis.eqTareWeightKg)
				}
			}
			println("After unitList"+chList.size());
