// Flush the unit before using its ufv - to avoid deadlock

class FlushUnit{
    public static String BEAN_ID = "FlushUnit"
    public void execute(TransactionAndVisitHolder dao, api) {

	TruckTransaction tran = dao.tran
        Container ctr = tran.tranContainer

        if (!ctr)
	return

        UnitFinder unitFinder = (UnitFinder) Roastery.getBean(UnitFinder.BEAN_ID)

        Unit unit = unitFinder.findActiveUnit(ContextHelper.getThreadComplex(), ctr)

        if (!unit)
	unit = unitFinder.findAdvisedUnitByLandModes(ContextHelper.getThreadComplex(), ctr)

	if (!unit)
        return

        // update the unit's flex-string and flush it

        String str = unit.getUnitFlexString15()
        if(str == null || "touchUnit".equals(str)) 
	    unit.setUnitFlexString15("flushUnit");
        else
	    unit.setUnitFlexString15("touchUnit");

        Roastery.getHibernateApi().flush();        
    }
}

import com.navis.argo.business.reference.Group;
import com.navis.inventory.business.units.Routing;
import com.navis.inventory.business.units.Unit;
import com.navis.road.business.adaptor.truckingcompany.RejectTruckingCompanyExpiredLineAgreement;
import com.navis.road.business.workflow.TransactionAndVisitHolder;
import java.util.ArrayList;

public class RejectTruckingCompanyExpiredLineAgreementGroovy extends RejectTruckingCompanyExpiredLineAgreement {
    public void execute(TransactionAndVisitHolder dao, api) {
        Unit unit = dao.getTran().getTranUnit();
        String groupId = getGroupId(unit);

        if (!GROUP_IDS.contains(groupId)) {
            execute(dao);
        }

    }

    private String getGroupId(Unit unit) {
        Routing routing = unit.getUnitRouting();
        if (routing == null) {
            return null;
        }

        Group group = routing.getRtgGroup();
        if (group == null) {
            return null;
        }

        return group.getGrpId();
    }

    private static final ArrayList GROUP_IDS = ["YB","XFER-SI","XFER-WO","XFER-P2"];
}

