public class GvyCmisTrade {
public String processTrade(Object unit, Object srv) { def trade = '' def loadPort = unit.getFieldValue("unitRouting.rtgPOL.pointId") def dischargePort = unit.getFieldValue("unitRouting.rtgPOD1.pointId") def destination = unit.getFieldValue("unitGoods.gdsDestination") try { if ('MAT'.equals(srv) || 'CRX'.equals(srv)) { trade = 'A' } else { // may need change for 'MSK' carrier, so else is left trade = 'A' } println("trade::after:" + trade); } catch (Exception e) { e.printStackTrace(); } return trade; }
