    public void init(){
        inj = new GroovyInjectionBase();
        gvyEventUtil = gvyEventUtil == null ? inj.getGroovyClassInstance("GvyEventUtil") : gvyEventUtil ;
        timezone = ContextHelper.getThreadUserTimezone();
