	    if(id.equalsIgnoreCase("MAJURO,MARSHALL ISLANDS")) return "Maj";
	    if(id.equalsIgnoreCase("Molokai,Hawaii")) return  "Mol";
	    if(id.equalsIgnoreCase("Honolulu, Hawaii")) return "Hon";
