			def tempTM3 = inUnit.getFieldValue("unitGoods.gdsReeferRqmnts.rfreqTimeMonitor3");
			def tempTM4 = inUnit.getFieldValue("unitGoods.gdsReeferRqmnts.rfreqTimeMonitor4");
