public class  MATProcessNewVess extends GroovyApi {

	UserContext context = ContextHelper.getThreadUserContext();
	Date timeNow = ArgoUtils.convertDateToLocalDateTime(ArgoUtils.timeNow(), context.getTimeZone());
	String startDate = null;
	String endDate = null;

	private static final String XML_OVERRIDE = "\"";
	private static final String honFacility = "ANK";
	private static final String XML_END_ELEMENT = "/>";
	private static final String queueName = "jms.tos.newvess.inbound";
    private static final String url = "tcp://10.201.1.79:61616";//Prod
	private static final String topicName = "jms.topic.tdp.newVesselHon";

	def inj = new GroovyInjectionBase();
	int pos
	private String addNode = "Y";

  	private final String  emailFrom = '1aktosdevteam@matson.com'
  	private final String emailTo = "1aktosdevteam@matson.com";

	Integer totalContainers = 0;


  public void execute(Unit tempUnit)
  {
    LOGGER.warn("MATProcessNewVess started" + timeNow);
	DateFormat dateFormat = new SimpleDateFormat("MM/dd/yyyy HH:mm:ss");
	Date date = new Date();
	startDate = dateFormat.format(date);

    if (tempUnit == null){
      LOGGER.warn (" Unit is null");
      return;
    }
    UnitFacilityVisit ufv = tempUnit.getUnitActiveUfvNowActive();
    if (ufv == null){
      LOGGER.warn (" Unit facility is null");
      return;
    }
    CarrierVisit cv = ufv.getInboundCarrierVisit();

	List unitListForUpdate = this.getUnitListInCvForUpdate(cv);
	if (unitListForUpdate.isEmpty()) {
      LOGGER.warn ("No Units in CV for update");
      return;
    }

	LOGGER.warn("unitListForUpdate.size" + unitListForUpdate.size);
	LOGGER.warn("unitListForUpdate Start");
	Iterator iter = unitListForUpdate.iterator();
		while(iter.hasNext()) {
		def units = iter.next();
		LOGGER.warn ("BLNumberInit " + units.unitId +" --- "+ units.getFieldValue("unitGoods.gdsBlNbr"));
		def BLNumber=units.getFieldValue("unitGoods.gdsBlNbr");
            //if(BLNumber.startsWith("Testing")) {
		if (BLNumber != null)
		{
			if(BLNumber.startsWith("DO NOT EDIT - WAIT FOR NEWVES")) {
				LOGGER.warn ("InBL Number " + units.unitId +" --- "+ units.getFieldValue("unitGoods.gdsBlNbr"));
				units.setFieldValue("unitGoods.gdsBlNbr", null);
            }
		}
		//units.setFieldValue("unitGoods.gdsBlNbr", "DO NOT EDIT - WAIT FOR NEWVES");
		LOGGER.warn ("BLNumber " + units.unitId +" --- "+ units.getFieldValue("unitGoods.gdsBlNbr"));
           }
	LOGGER.warn("unitListForUpdate End " + cv);

    List unitList = this.getUnitListInCv(cv);
    if (unitList.isEmpty()) {
      LOGGER.warn ("No Units in CV");
      return;
    }

	try
	{
		println("buildXmlElement Start " + cv);
		LOGGER.warn("buildXmlElement Start " + cv);
		String vesVoy = cv;
		StringBuffer xml = this.buildXmlElement(unitList, cv);
		LOGGER.warn("CLIENT_XML "+xml.toString());
		println("CLIENT_XML "+xml.toString());
		date = new Date();
		endDate = dateFormat.format(date);

		def emailSender = inj.getGroovyClassInstance("EmailSender")
		if ((xml.toString() != "null") || xml.toString().length() > 4)
		{
			//Send the Complete XML string to MNS application using JMS queue.
			this.send(xml.toString());
			this.topicSend(xml.toString());

			emailSender.custSendEmail(emailFrom,emailTo,"Client Vessel Notification for "+cv+" Count : "+totalContainers,"Clent vessel completed for "+cv+ " Count : "+totalContainers+" , xml posted to MNS application");
			println("CLIENT_VESSEL_STATUS: Clent vessel completed for "+cv+ " , xml posted to MNS application");
			try
			{
				def GvyRefDataLookup = inj.getGroovyClassInstance("GvyRefDataLookup");
				Connection conn;
			try {
				conn = GvyRefDataLookup.connect();
				GvyRefDataLookup.insertTdpLogData(vesVoy,"NV",startDate,endDate,"Processed",conn);
				println("GvyRefDataLookup.insertTdpLogData Calling");
			} finally {
				GvyRefDataLookup.disconnect(conn);
				println("GvyRefDataLookup.insertTdpLogData Ended");
			}

			}
			catch (e)
			{
				println("Error :::::::::::::::::::::::::::::::::::: GvyRefDataLookup.insertTdpLogData Ended " + e);
			}

		}
		else{
			emailSender.custSendEmail(emailFrom,emailTo,"Client Vessel Notification for "+cv +",Clent vessel completed, xml has not been posted to MNS application due to error in data");
			println("CLIENT_VESSEL_STATUS: Client Vessel Notification for "+cv+",Clent vessel completed, xml has not been posted to MNS application due to error in data");
		}
	}
	catch (xe)
	{
		LOGGER.warn("MATProcessNewVess error while building xml" + xe);
		println("MATProcessNewVess error while building xml" + xe);
	}

    LOGGER.warn("MATProcessNewVess ended" + timeNow);
  }

public void send(String msg) throws JMSException, Exception {
      LOGGER.warn (" in send message ");
      String  cfName= "jms.cf.oceanevent";
      Session                	session    = null;
      javax.jms.Connection	connection = null;
      ConnectionFactory      	cf         = null;
      MessageProducer        	mp         = null;
      Destination				destination = null;
	  println( "Calling MATProcessNewVess.send using ActiveMQConnectionFactory");
      try {
        LOGGER.warn( "Getting Connection Factory");
        cf = new ActiveMQConnectionFactory(url);
        LOGGER.warn( "Getting Queue");
        LOGGER.warn( "Getting Connection for Queue " + cf);
        connection = cf.createConnection();
        LOGGER.warn( "staring the connection");
        connection.start();
        LOGGER.warn( "creating session");
        session = connection.createSession(false, 1);
        LOGGER.warn( "creating messageProducer");
		destination = session.createQueue(queueName);
        mp = session.createProducer(destination);
		LOGGER.warn( "creating TextMessage");
        TextMessage outMessage = session.createTextMessage( msg);
        LOGGER.warn( "sending Message to queue: " + queueName);
        mp.send(outMessage);
        mp.close();
		session.close();
        connection.close();
      }
      catch (Exception je)
      {
        LOGGER.warn("Exception in send:" + je )
      }
    }

   public void topicSend(String msg) throws JMSException, Exception {
   	LOGGER.warn (" in topicSend message ");
  	TopicSession               session    = null;
  	TopicConnection             connection = null;
  	TopicConnectionFactory      cf         = null;
  	MessageProducer        mp         = null;
  	Destination            destination = null;
    println( "Calling MATProcessNewVess.topicSend using ActiveMQConnectionFactory");
  	try {

  		LOGGER.warn ("Getting Connection Factory");
  		cf = new ActiveMQConnectionFactory(url);

  		LOGGER.warn("Getting Connection for Topic");
  		connection = cf.createTopicConnection();

  		LOGGER.warn( "staring the connection");
  		connection.start();

  		LOGGER.warn( "creating session");
  		session = connection.createTopicSession(false, 1);
  		destination = session.createTopic(topicName);

  		LOGGER.warn( "creating messageProducer");
  		mp = session.createProducer(destination);

  		LOGGER.warn( "creating TextMessage");
  		TextMessage outMessage = session.createTextMessage( msg);

  		LOGGER.warn( "sending Message to topic: " + topicName);
  		mp.send(outMessage);

  		mp.close();
  		session.close();
  		connection.close();
      }
      catch (Exception je)
      {
  	LOGGER.warn("Exception in topicSend:" + je )
      }
}

