public StringBuffer geTimeStamp(Unit inUnit, StringBuffer inElementList, CarrierVisit inCv)
	{

		def ufv = inUnit.getUnitActiveUfvNowActive();
		if (ufv !=null)
		{
			String timeIn = ufv.ufvTimeIn;
			String timeOut = ufv.ufvTimeOut;
			String timeLoad = ufv.ufvTimeOfLoading;

		    String timeStampAttrOpn = "<timestamps";
			String timeStampString = timeStampAttrOpn;
			if (timeIn !=null)
			{
				timeStampString = timeStampString + " time-in=" + XML_OVERRIDE + timeIn + XML_OVERRIDE;
			}
			if (timeOut !=null)
			{
				timeStampString = timeStampString + " time-out=" + XML_OVERRIDE + timeOut + XML_OVERRIDE;
			}
			if (timeLoad !=null)
			{
				timeStampString = timeStampString + " time-load=" + XML_OVERRIDE + timeLoad + XML_OVERRIDE;
			}
			if (timeIn != null || timeOut != null || timeLoad != null)
			{
				timeStampString = timeStampString + XML_END_ELEMENT;
				inElementList.append(timeStampString);
			}
		}
		return inElementList;
	  }

public String stripInvalidXmlCharacters(String input){
    String specialCharacter = "&";
    try {
    if (input.contains(specialCharacter)) {
       input = input.replaceAll("&", "&amp;");
       //LOGGER.warn("sb:" + input);
    }

	if (input.contains("null")) {
       input = input.replaceAll("null", " ");
       //LOGGER.warn("sb:" + input);
    }
    }
    catch (e){

    }
    return input;
  }

 public String removeDoubleQuotes(String input){
    String specialCharacter = "&";
    try {
    if (input.contains("\"")) {
       input = input.replaceAll("\"", "#quot;");
    }
	if (input.contains("<")) {
       input = input.replaceAll("<", "#lt;");
    }
	if (input.contains(">")) {
       input = input.replaceAll(">", "#gt;");
    }
    }
    catch (e){

    }
    return input;
  }

 public String ModifyEscapeChar(String input){
    try {
    if (input.contains("#quot")) {
       input = input.replaceAll("#quot;", "&quot;");
    }
	if (input.contains("#lt")) {
       input = input.replaceAll("#lt;", "&lt;");
    }
	if (input.contains("#gt")) {
       input = input.replaceAll("#gt;", "&gt;");
    }
    }
    catch (e){

    }
    return input;
  }

    private static final Logger LOGGER = Logger.getLogger(MATProcessNewVess.class);
}

import java.sql.Connection;
import java.sql.PreparedStatement;
import java.sql.ResultSet;
import javax.sql.DataSource;
import javax.naming.Context;
import javax.naming.InitialContext;

/**
*  A1 SKB 05/11/2009  Added POD lookup
*  A2 SKB 06/25/2009  Removed connection to prevent memory leak
*  A3 GR  10/21/10    Added Chassis RFID WSDL lookup Code
                      Added Env Spec IP lookup code for JMS Server Connectivity
*  A4 GR  08/19/11    Added ActiveMQ InitiaContextFactory Lookup
*/
class GvyMNSRefDataLookup {


	public Connection connect() {

			// Obtain our environment naming context
			Context initCtx = new InitialContext();
			Context envCtx = (Context) initCtx.lookup("java:comp/env");

			// Look up our data source
			DataSource ds = (DataSource)envCtx.lookup("jdbc.ds.tdp.nontx");

			// Allocate and use a connection from the pool
			Connection conn = ds.getConnection();
			return conn;

	}

	public void disconnect(Connection conn) {
		if(conn != null) conn.close();
	}

	public String lookup(String key, Connection conn) {
		PreparedStatement stmt = null;
		ResultSet rs = null;
		try {
		  String sql = "select value from TOS_APP_PARAMETER where key = ?";
		  stmt = conn.prepareStatement(sql);
		  stmt.setString(1,key);
		  //rs = stmt.executeQuery();
	      //if(rs.next()) {
	    //	  return rs.getString(1);
	     // }

	    } finally {
	    	if(stmt != null) stmt.close();
	    	//if(rs != null )rs.close();
	    }
	    return null;
	}



// Code for getNowServer
    public static String host;
	public static boolean dynamic = true;

	public static synchronized void initName() {
		GvyMNSRefDataLookup lookup = new GvyMNSRefDataLookup();
		Connection conn;
		try {
			conn = lookup.connect();
			host = lookup.lookup("NOW_URL", conn);

			String dynamicString =  lookup.lookup("NOW_DYNAMIC",conn);
			dynamic = Boolean.parseBoolean(dynamicString);
		    println("DEBUG UPDT getNowServer "+host);

		} finally {
			lookup.disconnect(conn);
		}
		//println("initNowServer")
	}

	public getNowServer() {

		if(dynamic == true) {
			initName();
		}
		return host;

	}

	//A3 
	public static String chasRfidHost;
	public static String n4TopicHost;
	public static boolean chasRfidDynamic = true;

	public static synchronized void initChasRfid() {
		GvyMNSRefDataLookup lookup = new GvyMNSRefDataLookup();
		Connection conn;
		try {
			conn = lookup.connect();
			chasRfidHost = lookup.lookup("CHAS_RFID_NOW_URL", conn);

			String dynamicChasStr =  lookup.lookup("NOW_DYNAMIC",conn);
			chasRfidDynamic = Boolean.parseBoolean(dynamicChasStr);
		    println("DEBUG UPDT getNowServer "+chasRfidHost);

		} finally {
			lookup.disconnect(conn);
		}
		//println("initNowServer")
	}

	public getChasRfidUrl() {

		if(chasRfidDynamic == true) {
			initChasRfid();
		}
		//println("Called getChasRfidUrl");
		return chasRfidHost;

	}


	public static synchronized String getN4TopicAddress() {
		GvyMNSRefDataLookup lookup = new GvyMNSRefDataLookup();
		Connection conn;
		try {
			conn = lookup.connect();
			n4TopicHost = lookup.lookup("JMS_URL", conn);

		} finally {
			lookup.disconnect(conn);
		}
       return n4TopicHost
	}

    //A3 Ends
}

