public void detentionDischPortMsg(Object gvyCmisUtil,Object gvyEventUtil,Object event,Object gvyBaseClass,Object unit)
{
  try
  {
       //latest Code Updt Starts
       prevDischPort = prevDischPort != null ? prevDischPort : ''
