/**
 * Created by VNatesan on 9/29/2016.
 */
class MATDetachContAcc {

    private Logger LOGGER = Logger.getLogger(MATDetachContAcc.class);

    public void execute(Object event) {
        LOGGER.setLevel(Level.INFO);
        LOGGER.info("MATDetachContAcc called on UNIT_LOAD to check and remove container accessories");

        Unit unit = (Unit) event.getEntity();

        if (unit == null) {
            return;
        }
        UnitFacilityVisit ufv = null;
        UnitEquipment ue = unit.getAccessoryOnCtr();
        if (ue != null && ue.hasNotBeenDetached()) {
            ufv = unit.getUnitActiveUfvNowActive();
        }
        if (ufv == null) {
            //throw BizFailure.create("Unit.dismount - no Active UFV for Unit: " + unit.getUnitId());
            LOGGER.info("Unit.dismount - no Active UFV for Unit: " + unit.getUnitId());
        } else {

           if (ufv.isTransitStateAtMost(UfvTransitStateEnum.S60_LOADED)) {
                LOGGER.info("MATDetachContAcc  isTransitStateAtMost ");
                //get position and complex,facility
                LocPosition currentPos = unit.findCurrentPosition();
                Facility facility = currentPos.resolveFacility();
                Complex complex = facility.getFcyComplex();

                //get equipment to detatch
                Equipment eqToDetach = ue.getUeEquipment();
                //detach the container accessories
                ue.detach(ue.getUeEquipment().getEqIdFull() + " dismounted");

                HibernateApi.getInstance().flush();
                String note = "detached from " + unit.getUnitId();

                LOGGER.info("MATDetachContAcc " + note);
                //create dummy truck
                CarrierVisit dummyTruck = CarrierVisit.getGenericTruckVisit(complex);
                //create new unit
                Unit newUnit = unit.createContainerizedUnit(note, complex, dummyTruck, dummyTruck, ue.getUeEquipment(), unit.getUnitLineOperator());
                LocPosition inboundPosition = LocPosition.createLocPosition(dummyTruck, (String)null, (String)null);
                UnitFacilityVisit newUfv = UnitFacilityVisit.createUnitFacilityVisit(newUnit, facility, inboundPosition, dummyTruck);

                UnitEquipment newUe1 = newUnit.getCurrentlyAttachedUe(eqToDetach);

                if(newUe1.getUeEquipmentState().getEqsEqOperator() != null) {
                    newUnit.setUnitLineOperator(newUe1.getUeEquipmentState().getEqsEqOperator());
                } else {
                    newUnit.setUnitLineOperator(newUe1.getUeEquipmentState().getEqsEqOwner());
                }

                RectifyParms parms2 = new RectifyParms();
                parms2.setUfvTransitState(UfvTransitStateEnum.S40_YARD);

                //since there is no default slot for Accessory when divorced ,i reused chassis dismount slot to accessory as well
                String accSlot1 = InventoryConfig.CHASSIS_DISMOUNT_SLOT.getSetting(ContextHelper.getThreadUserContext());
                if("GEN_YARD".equals(accSlot1)) {
                    accSlot1 = null;
                }
                newUfv.rectify(parms2);
                parms2.setSlot(accSlot1);
                newUnit.updateCategory(UnitCategoryEnum.STORAGE);
                newUnit.updateFreightKind(FreightKindEnum.MTY);
                HibernateApi.getInstance().update(newUnit);
            }


        }

    }

}

