public class GvyPodValidationForVesselUnit extends GroovyInjectionBase {
	def inj = new GroovyInjectionBase();

	public void execute(Map parameters)       {

		log("inside GvyPodValidationForVesselUnit");
		ArrayList unitRptList = new ArrayList();
		Long facilityGkey = ContextHelper.getThreadFacility().getFcyGkey();
		log("Started : GvyPodValidationForVesselUnit" +"facilityGkey-->"+facilityGkey +"id"+ContextHelper.getThreadFacility().getFcyId());
		try{

			List acctList = null;
			List resultAcctList = new ArrayList();
			def servicesMgr = (ServicesManager)Roastery.getBean("servicesManager");
			DomainQuery dq = QueryUtils.createDomainQuery("UnitFacilityVisit")
					.addDqPredicate(PredicateFactory.eq(UnitField.UFV_FACILITY_ID, ContextHelper.getThreadFacility().getFcyId()))
					.addDqPredicate(PredicateFactory.eq(UnitField.UFV_ACTUAL_OB_CARRIER_MODE, LocTypeEnum.VESSEL))
					.addDqPredicate(PredicateFactory.eq(UnitField.UFV_UNIT_VISIT_STATE, UnitVisitStateEnum.ACTIVE))
					.addDqPredicate(PredicateFactory.eq(UnitField.UFV_TRANSIT_STATE, UfvTransitStateEnum.S40_YARD))
					.addDqPredicate(PredicateFactory.in(UnitField.UFV_UNIT_CATEGORY, UnitCategoryEnum.EXPORT,UnitCategoryEnum.STORAGE))
					.addDqOrdering(Ordering.desc(InventoryField.UFV_TIME_OF_LAST_MOVE));


			acctList = HibernateApi.getInstance().findEntitiesByDomainQuery(dq);
			log("DomainQuery===="+dq);

			if(acctList!=null){
				log("acctList size "+acctList.size());
			}
			Iterator unitIter =acctList!=null ?acctList.iterator() :null;

			while( unitIter!=null && unitIter.hasNext()) {
				def ufv = unitIter.next();
				def unit = ufv!=null ? ufv.ufvUnit :null;


				if(unit==null || unit==""){
					log(" invalid unit found "+unit);
					continue;
				}

				def equiClass =unit.getFieldValue("unitPrimaryUe.ueEquipment.eqEquipType.eqtypClass");
				equiClass = equiClass != null ? equiClass.getKey() : '';

				def unitVisitState = unit.getFieldValue("unitVisitState");
				def unitCategory = unit.getFieldValue("unitCategory");

				log("unit "+unit.unitId + " category "+ unitCategory +" state "+ unitVisitState +" equiClass "+equiClass);

				if(unitVisitState==null || unitVisitState=="" || unitCategory==null || unitCategory=="" ){

					log(" found invalid state for unit "+unit.unitId + " category "+unitCategory +" state "+ unitVisitState);
					continue;
				}


				if(unit!=null  && com.navis.inventory.business.atoms.UnitVisitStateEnum.ACTIVE.equals(unitVisitState) &&

				(UnitCategoryEnum.STORAGE.equals(unitCategory)  ||	UnitCategoryEnum.EXPORT.equals(unitCategory) )  && 'CONTAINER'.equals(equiClass) ){

					log(" inside unit "+unit.unitId + " category "+ unitCategory +" state " +unitVisitState);

					def intdObCarrierId=unit.getFieldValue("unitActiveUfv.ufvActualObCv.cvId");

					UnitFacilityVisit Unitufv = unit.getUnitActiveUfvNowActive();

					CarrierVisit actualObCv =Unitufv!=null && Unitufv!=""? Unitufv.getUfvObCv():null;

					log("actualObCv: "+actualObCv +" intdObCarrierId -->"+intdObCarrierId);

					if(actualObCv!=null && actualObCv!=""){

						final LocTypeEnum carrierMode = actualObCv.getCvCarrierMode();

						log("carrierMode: "+carrierMode +"unit id "+unit);

						if (carrierMode!=null && carrierMode.equals(LocTypeEnum.VESSEL)){

							def pod= unit.getFieldValue("unitRouting.rtgPOD1.pointId");

							if(pod==null || pod=="")
							{
								log("Outbound carrier for unitId: "+unit.unitId + " carrierMode "+carrierMode +"pod-->"+pod  +" visit state "+unitVisitState);

								log("applying hold for the unit id " +unit.unitId +" carrierMode " +carrierMode +"");

								servicesMgr.applyHold("PODCHECKFORVESS", unit, "Applied Hold by PODCHECKFORVESS Hold", "Applied Hold by PODCHECKFORVESS Hold", "Applied Hold by PODCHECKFORVESS Hold");

								log("applied hold for the unit id " +unit.unitId +" carrierMode " +carrierMode +"");

								resultAcctList.add(unit);

							}
						}

					}

				}
			}

			log("End GvyPodValidationForVesselUnit updated hold for the number of units "+resultAcctList.size());

		} catch (Exception e){
			log("Exception in GvyPodValidationForVesselUnit "+e.printStackTrace());
			log("Exception in GvyPodValidationForVesselUnit "+e.getMessage());

		}
	}




}

/*
* SrNo       Name       Date             Comments
*  A1         GR           03/228/09      Updt ReplaceQuotes Method
*/
public class GvyCmisTxtMsgFormatter {

  public String doIt(Object attrName,Object attrValue ) 
  {
     def attrValueFmt = ''
     def fmtValue = ''
     try
    { 
       //Converting value to String
        attrValue = replaceQuotes(''+attrValue)

        if(attrValue == null || attrValue.trim().length() == 0){
            attrValueFmt = 'null'
         }else{
            attrValueFmt = attrValue  
         }

      fmtValue = attrName+'=\''+attrValueFmt+'\' '
    }catch(Exception e){
        e.printStackTrace()
    }

     return fmtValue;
   }

    public  String replaceQuotes(Object message)
   {
     def msg = message.toString();
     def replaceAmp = msg.replaceAll('&', '&amp;');
           replaceAmp = replaceAmp.replaceAll('\'', '&apos;');
           replaceAmp = replaceAmp.replaceAll("<", "&lt;")
           replaceAmp =  replaceAmp.replaceAll(">", "&gt;")
           replaceAmp = replaceAmp.replaceAll("\"", "&quot;")
     return replaceAmp; 
    }

   public String  createGroovyXml(String msgString)
  {
     def strBuff = new StringBuffer();
     strBuff.append( '<GroovyMsg');
     strBuff.append(' '+msgString);
     strBuff.append( '/>');
     return strBuff.toString();
   }

}//Class Ends

