//Class Formats the message string by appending Attr & Attr value 

public class GvyMsgFormatter {

   public String doIt(String attrName,Object attrValue ) 
  {
       def fmtValue = ''
       if(attrValue != null)
       {	
        fmtValue = attrName+'=\''+attrValue+'\' '
        return fmtValue;
       }
       else
       {
         return fmtValue;
        }
   }
}
