import com.navis.apex.business.model.GroovyInjectionBase

public class GvyUnitEquiDetail {

public String doIt(Object unitObj)    
{
   println("In Class GvyUnitEquiDetail.doIt()")
   //Calling Msg Formater class
   def gvyBase = new GroovyInjectionBase()
   def gvyMsgFmt = gvyBase.getGroovyClassInstance("GvyMsgFormatter");

   def u = unitObj
   def _unitId=u.getFieldValue("unitId")
   def unitId = gvyMsgFmt.doIt('unitId',_unitId)
   def _time=u.getFieldValue("unitCreateTime")
   def time =  gvyMsgFmt.doIt('time', _time.toString())
   def eventCreator= gvyMsgFmt.doIt('eventCreator', 'TESTER')
   def _equiClass =u.getFieldValue("unitPrimaryUe.ueEquipment.eqEquipType.eqtypClass")
   if(_equiClass != null)
   _equiClass = _equiClass.getKey()
   def equiClass = gvyMsgFmt.doIt('equiClass',_equiClass)
   def _equiTareKg=u.getFieldValue("unitPrimaryUe.ueEquipment.eqTareWeightKg")
   def equiTareKg = gvyMsgFmt.doIt('equiTareKg',_equiTareKg)
   def _equiHeightmm=u.getFieldValue("unitPrimaryUe.ueEquipment.eqHeightMm")
   def equiHeightmm = gvyMsgFmt.doIt('equiHeightmm',_equiHeightmm)
   def _equiStrengthCode=u.getFieldValue("unitPrimaryUe.ueEquipment.eqStrengthCode")
   def equiStrengthCode = gvyMsgFmt.doIt('equiStrengthCode',_equiStrengthCode)
   def _equiOwner =u.getFieldValue("unitPrimaryUe.ueEquipmentState.eqsEqOwner.bzuId")
   def equiOwner = gvyMsgFmt.doIt('equiOwner',_equiOwner)
   def _equiOperator=u.getFieldValue("unitPrimaryUe.ueEquipmentState.eqsEqOperator.bzuId")
   def equiOperator = gvyMsgFmt.doIt('equiOperator',_equiOperator)
   def _equiMaterial=u.getFieldValue("unitPrimaryUe.ueEquipment.eqMaterial")
   if(_equiMaterial != null)
   _equiMaterial = _equiMaterial.getKey()
   def equiMaterial = gvyMsgFmt.doIt('equiMaterial',_equiMaterial)
   def _holdId=u.getFieldValue("unitAppliedHoldOrPermName")
   def holdId = gvyMsgFmt.doIt('holdId',_holdId)
   def _severity=u.getFieldValue("unitPrimaryUe.ueEquipmentState.eqsDamageSeverity")
   if(_severity != null)
   _severity = _severity.getKey()
   def severity = gvyMsgFmt.doIt('severity',_severity)

   def equiAttr = unitId+time+eventCreator+equiClass+equiTareKg+equiHeightmm+equiStrengthCode+equiOwner+equiOperator+equiMaterial+holdId+severity

   println(equiAttr)
   return equiAttr;
 }

}
