public class GvyUnitHazards {

 public String doIt(Object obj) 
 {
  println("In Class GvyUnitHazards.doIt()")
  def gvyBase = new GroovyInjectionBase()
  def gvyMsgFmt = gvyBase.getGroovyClassInstance("GvyMsgFormatter");

  def u = obj
  def _isHazardous=u.getFieldValue("unitGoods.gdsIsHazardous")
  def isHazardous = gvyMsgFmt.doIt('isHazardous',_isHazardous)

  def _hazImdg=u.getFieldValue("unitGoods.goodsThreeMainHazardCodes")
  def mostHazItem =   getMostHazMaterial(_hazImdg)
  def hazImdg = gvyMsgFmt.doIt('hazImdg',mostHazItem)

 def _hazUnNum=u.getFieldValue("unitGoods.goodsThreeMainHazardUNNumbers")
  def mostHazNum =   getMostHazMaterial(_hazUnNum)
  def hazUnNum = gvyMsgFmt.doIt('hazUnNum',mostHazNum)

  def hazAttr =  isHazardous+hazImdg+hazUnNum
  println(hazAttr)
  println("xx")
  return hazAttr;

 }

 public String getMostHazMaterial(String hazItem)
{
   def pattern = ','

   if(hazItem != null){
     for(i in hazItem){
       if(i == pattern){
       def mostHazItem = hazItem.substring(0, hazItem.indexOf(i));
       return mostHazItem; 
      }
     }
   }
  return null; 
}



}

