import com.navis.apex.business.model.GroovyInjectionBase

public class GvyUnitEventProcessor {

public String doIt(String messageType,Object unitObj)    
{
 def unit = unitObj
 println("In Class GvyUnitEventProcessor.doIt()")
//Calling Msg Formater class
def gvyBaseClass = new GroovyInjectionBase()
def gvyTxtMsgFmt = gvyBaseClass.getGroovyClassInstance("GvyMsgFormatter");
def msgType =  gvyTxtMsgFmt.doIt('msgType',messageType)

gvyBaseClass.log("Start Processing GvyUnitEventProcessor")

def unitEquip = gvyBaseClass.getGroovyClassInstance("GvyUnitEquiDetail");
def equipDetail = unitEquip.doIt(unit);

def unitRtgBound = gvyBaseClass.getGroovyClassInstance("GvyUnitRtgBoundDetails");
def rtgBoundDetails =unitRtgBound.doIt(unit);

def unitSealAttr = gvyBaseClass.getGroovyClassInstance("GvyUnitSeal");
def sealAttr =unitSealAttr.doIt(unit);

def unitPhyStatus = gvyBaseClass.getGroovyClassInstance("GvyUnitPhysicalStatus");
def phyStatusAttr =unitPhyStatus.doIt(unit);

def unitshipmentDetails = gvyBaseClass.getGroovyClassInstance("GvyUnitShipmentDetails");
def shipmentDetails =unitshipmentDetails.doIt(unit);

def unitReefer = gvyBaseClass.getGroovyClassInstance("GvyUnitReefer");
def unitReeferAttr =unitReefer.doIt(unit);

def unitRouting = gvyBaseClass.getGroovyClassInstance("GvyUnitRouting");
def routingAttr =unitRouting.doIt(unit);

def unitposition = gvyBaseClass.getGroovyClassInstance("GvyUnitPosition");
def positionAttr =unitposition.doIt(unit);

def unitHazards = gvyBaseClass.getGroovyClassInstance("GvyUnitHazards");
def hazardsAttr =unitHazards.doIt(unit);

def unitOverDimension = gvyBaseClass.getGroovyClassInstance("GvyUnitOverDimension");
def overDimensionAttr =unitOverDimension.doIt(unit);

def unitSparcsDetail = gvyBaseClass.getGroovyClassInstance("GvyUnitSparcsDetail");
def sparcsDetailAttr =unitSparcsDetail.doIt(unit);

def ufvFlexFields = gvyBaseClass.getGroovyClassInstance("GvyUfvFlexFields");
def ufvFlexFieldsAttr =ufvFlexFields.doIt(unit);

def unitFlexFields = gvyBaseClass.getGroovyClassInstance("GvyUnitFlexFields");
def unitFlexFieldsAttr =unitFlexFields.doIt(unit);

def msgString = msgType+' '+equipDetail+' '+rtgBoundDetails+' '+sealAttr+' '+phyStatusAttr+' '+shipmentDetails+' '+unitReeferAttr+' '+routingAttr+' '+positionAttr+' '+hazardsAttr+' '+overDimensionAttr+' '+sparcsDetailAttr+' '+ufvFlexFieldsAttr+' '+unitFlexFieldsAttr

def gvyMsgFmt = gvyBaseClass.getGroovyClassInstance("GvyXmlMsgFormatter");
def groovyXml =gvyMsgFmt.doIt(msgString);

gvyBaseClass.log("After Processing : "+groovyXml)

gvyBaseClass.sendXml(groovyXml)

 }
}
