import com.navis.apex.business.model.GroovyInjectionBase

public class GvyUnitReefer {

 public String doIt(Object obj) 
 {
  println("In Class GvyUnitReefer.doIt()")
  def gvyBase = new GroovyInjectionBase()
  def gvyMsgFmt = gvyBase.getGroovyClassInstance("GvyMsgFormatter");

  def u = obj
  def _requiresPower=u.getFieldValue("unitRequiresPower")
  def requiresPower = gvyMsgFmt.doIt('requiresPower',_requiresPower) 
  def _tempReqdC=u.getFieldValue("unitGoods.gdsReeferRqmnts.rfreqTempRequiredC")
  def tempReqdC = gvyMsgFmt.doIt('tempReqdC',_tempReqdC) 
  def _o2Pct=u.getFieldValue("unitGoods.gdsReeferRqmnts.rfreqO2Pct")
  def o2Pct = gvyMsgFmt.doIt('o2Pct',_o2Pct) 
  def _co2Pct=u.getFieldValue("unitGoods.gdsReeferRqmnts.rfreqCO2Pct")
  def co2Pct = gvyMsgFmt.doIt('co2Pct',_co2Pct) 
  def _humidityPct=u.getFieldValue("unitGoods.gdsReeferRqmnts.rfreqHumidityPct")
  def humidityPct = gvyMsgFmt.doIt('humidityPct',_humidityPct) 
  def _ventRequiredValue=u.getFieldValue("unitGoods.gdsReeferRqmnts.rfreqVentRequired")
  def ventRequiredValue = gvyMsgFmt.doIt('ventRequiredValue',_ventRequiredValue)
  def _ventRequiredUnit=u.getFieldValue("unitGoods.gdsReeferRqmnts.rfreqVentUnit")
  def ventRequiredUnit = gvyMsgFmt.doIt('ventRequiredUnit',_ventRequiredUnit)
  def _tempMinC=u.getFieldValue("unitGoods.gdsReeferRqmnts.rfreqTempLimitMaxC")
  def tempMinC = gvyMsgFmt.doIt('tempMinC',_tempMinC)
  def _tempMaxC=u.getFieldValue("unitGoods.gdsReeferRqmnts.rfreqTempLimitMinC")
  def tempMaxC = gvyMsgFmt.doIt('tempMaxC',_tempMaxC)
  def _timeLatestOnPower=u.getFieldValue("unitGoods.gdsReeferRqmnts.rfreqLatestOnPowerTime")
  def timeLatestOnPower = gvyMsgFmt.doIt('timeLatestOnPower',_timeLatestOnPower)

  def reeferAttr = requiresPower+tempReqdC+o2Pct+co2Pct+humidityPct+ventRequiredValue+ventRequiredUnit+tempMinC+tempMaxC+timeLatestOnPower
