import com.navis.apex.business.model.GroovyInjectionBase

public class GvyUnitRouting {

 public String doIt(Object obj) 
 {
  println("In Class GvyUnitRouting.doIt()")
  def gvyBase = new GroovyInjectionBase()
  def gvyMsgFmt = gvyBase.getGroovyClassInstance("GvyMsgFormatter");

  def u = obj
  def _category=u.getFieldValue("unitCategory")
  _category = _category.getKey() 
  def category = gvyMsgFmt.doIt('category',_category)
  def _drayStatus=u.getFieldValue("unitDrayStatus")
  if(_drayStatus!= null)
  _drayStatus = _drayStatus.getKey()
  def drayStatus = gvyMsgFmt.doIt('drayStatus',_drayStatus)
  def _group=u.getFieldValue("unitRouting.rtgGroup.grpId")
  def group = gvyMsgFmt.doIt('group',_group)
  def _opl=u.getFieldValue("unitRouting.rtgOPL.pointId")
  def opl = gvyMsgFmt.doIt('opl',_opl)
  def _pod1=u.getFieldValue("unitRouting.rtgPOD1.pointId")
  def pod1 = gvyMsgFmt.doIt('pod1',_pod1)
  def _pod2=u.getFieldValue("unitRouting.rtgPOD2.pointId")
  def pod2 = gvyMsgFmt.doIt('pod2',_pod2)
  def _pol=u.getFieldValue("unitRouting.rtgPOL.pointId")
  def pol = gvyMsgFmt.doIt('pol',_pol)
  def _origin=u.getFieldValue("unitGoods.gdsOrigin")
  def origin = gvyMsgFmt.doIt('origin',_origin)
  def _destination=u.getFieldValue("unitGoods.gdsDestination")
  def destination = gvyMsgFmt.doIt('destination',_destination)
  def _designatedTrucker=u.getFieldValue("unitRouting.rtgTruckingCompany")
  def designatedTrucker = gvyMsgFmt.doIt('designatedTrucker',_designatedTrucker)

  def routingAttr = category+drayStatus+group+opl+pod1+pod2+pol+origin+destination+designatedTrucker
  println(routingAttr)
  return routingAttr;
 }
}
