import com.navis.apex.business.model.GroovyInjectionBase

//class contains inbound and outbound routing attributes 

public class GvyUnitRtgBoundDetails {

   public String doIt(Object unitObj) 
  {
    println("In Class GvyUnitRtgBoundDetails.doIt()")
    //Calling Msg Formater class
   def gvyBase = new GroovyInjectionBase()
   def gvyMsgFmt = gvyBase.getGroovyClassInstance("GvyMsgFormatter");	

    def u = unitObj
    def _dibcarrierId=u.getFieldValue("unitDeclaredIbCv.cvId")
    def dibcarrierId = gvyMsgFmt.doIt('dibcarrierId',_dibcarrierId)    
    def _dibMode=u.getFieldValue("unitDeclaredIbCv.cvCarrierMode")
    _dibMode = _dibMode.getKey()
    def dibMode = gvyMsgFmt.doIt('dibMode',_dibMode)    
    def _aibcarrierId=u.getFieldValue("unitActiveUfv.ufvActualIbCv.cvId")
    def aibcarrierId = gvyMsgFmt.doIt('aibcarrierId',_aibcarrierId)     
    def _aibMode=u.getFieldValue("unitActiveUfv.ufvActualIbCv.cvCarrierMode")
    _aibMode = _aibMode.getKey()
    def aibMode = gvyMsgFmt.doIt('aibMode',_aibMode)
    def _dobcarrierId=u.getFieldValue("unitRouting.rtgDeclaredCv.cvId")
    def dobcarrierId = gvyMsgFmt.doIt('dobcarrierId',_dobcarrierId)
    def _dobMode=u.getFieldValue("unitRouting.rtgDeclaredCv.cvCarrierMode")
    _dobMode = _dobMode.getKey()
    def dobMode = gvyMsgFmt.doIt('dobMode',_dobMode)
    def _aobcarrierId=u.getFieldValue("unitActiveUfv.ufvActualObCv.cvId") 
    def aobcarrierId = gvyMsgFmt.doIt('aobcarrierId',_aobcarrierId)
    def _aobMode=u.getFieldValue("unitActiveUfv.ufvActualObCv.cvCarrierMode")
    _aobMode = _aobMode.getKey() 
    def aobMode = gvyMsgFmt.doIt('aobMode',_aobMode)

    def rtgBoundAttr = dibcarrierId+dibMode+aibcarrierId+aibMode+dobcarrierId+dobMode+aobcarrierId+aobMode  

    println(rtgBoundAttr)

    return rtgBoundAttr;
   }

}
