import com.navis.apex.business.model.GroovyInjectionBase

public class GvyUnitSeal {

 public String doIt(Object obj) 
 {
  println("In Class GvyUnitSeal.doIt()")
  def gvyBase = new GroovyInjectionBase()
  def gvyMsgFmt = gvyBase.getGroovyClassInstance("GvyMsgFormatter");

  def u = obj
  def _seal1=u.getFieldValue("unitSealNbr1")
  def seal1 = gvyMsgFmt.doIt('seal1',_seal1)
  def _seal2=u.getFieldValue("unitSealNbr2")
  def seal2 = gvyMsgFmt.doIt('seal2',_seal2)
  def _seal3=u.getFieldValue("unitSealNbr3")
  def seal3 = gvyMsgFmt.doIt('seal3',_seal3)
  def _seal4=u.getFieldValue("unitSealNbr4")
  def seal4 = gvyMsgFmt.doIt('seal4',_seal4)

  def sealAttr = seal1+seal2+seal3+seal4
  println(sealAttr)
  return sealAttr;
  }
}
