import com.navis.apex.business.model.GroovyInjectionBase

public class GvyUnitSparcsDetail {

 public String doIt(Object obj) 
 {
  println("In Class GvyUnitSparcsDetail.doIt()")
  def gvyBase = new GroovyInjectionBase()
  def gvyMsgFmt = gvyBase.getGroovyClassInstance("GvyMsgFormatter");

  def u = obj
  def _timeIn=u.getFieldValue("unitActiveUfv.ufvTimeIn")
  def timeIn = gvyMsgFmt.doIt('timeIn',_timeIn)
  def _timeOut=u.getFieldValue("unitActiveUfv.ufvTimeOut")
  def timeOut = gvyMsgFmt.doIt('timeOut',_timeOut)
  def _timeLoad=u.getFieldValue("unitActiveUfv.ufvTimeOfLoading")
  def timeLoad = gvyMsgFmt.doIt('timeLoad',_timeLoad)

  def spacrsDetails = timeIn+timeOut+timeLoad 
  println(spacrsDetails)
  return spacrsDetails;
 }
}
