public class GvyXmlMsgFormatter {

  public String doIt(Object obj) 
  {
    println("In Class GvyXmlMsgFormatter.doIt()")
    def message = replaceAmpersand(obj)
    def gvyXmlMsg = createGroovyXml(message)
    return gvyXmlMsg;
  }

  public String  createGroovyXml(String msgString)
  {
     def strBuff = new StringBuffer();
     strBuff.append( '<GroovyMsg');
     strBuff.append(' '+msgString);
     strBuff.append( '/>');
     return strBuff.toString();
   }

   public  String replaceAmpersand(String message)
   {
     def replaceAmp = message.replaceAll('&', '&amp;');
     return replaceAmp; 
    }

}
